import React, { Component, MouseEvent, RefObject } from 'react';

import linkIsExternal from 'utils/(to)';

import { Link } from 'react-router-dom';

type SharedContentProps = {
  children: React.ReactNode;
} | {
  text: React.ReactNode;
}

type SharedProps = SharedContentProps & {
  className: string,
  ariaLabel: string,
};

type ButtonProps = {
  onClick(e: MouseEvent<HTMLElement>): void;
  elemRef?: RefObject<HTMLButtonElement>;
  type: 'button' | 'submit' | 'reset';
}

type InternalLinkProps = {
  internalTo: string;
  elemRef?: RefObject<Link>;
}

type ExternalLinkProps = {
  externalTo: string;
  elemRef?: RefObject<HTMLAnchorElement>;
}

type UniqueProps = ButtonProps | InternalLinkProps | ExternalLinkProps;

type Props = SharedProps & UniqueProps;


const defaultProps = {
  className: '',
  type: 'button',
  to: '',
  onClick: () => {}
};

class Button extends Component<Props> {
  static defaultProps = defaultProps;

  render() {
    if ('internalTo' in this.props) {
      this.props.internalTo
    }

    if (to) {
      if (linkIsExternal(to)) {
        return (
          <a
            href={to}
            target="_blank"
            rel="noopener noreferrer"
            aria-label={ariaLabel}
            ref={elemRef as RefObject<HTMLAnchorElement>}
          >
            <div className={className}>
              {text ? text : children}
            </div>
          </a>
        );
      } else {
        return (
          <Link
            to={to}
            aria-label={ariaLabel}
            ref={elemRef as RefObject<Link>}
          >
            <div className={className}>
              {text ? text : children}
            </div>
          </Link>
        );
      }
    }

    return (
      <button
        className={className}
        aria-label={ariaLabel}
        onClick={onClick}
        type={type}
        ref={elemRef as RefObject<HTMLButtonElement>}
      >
        {text ? text : children}
      </button>
    );
  }
}

export default Button;
