import React, { createRef } from 'react';
import { Button } from 'components';
import { Link } from 'react-router-dom';
import { array } from 'prop-types';

function map<T>(array: Array<T>, func: (item: T) => any) {
  return array.forEach(func);
};

const thing = map([1, 's', 3], s => s + 1);



class App extends React.Component {

  onClickButtonRef = createRef<HTMLButtonElement>();
  reactLinkRef = createRef<Link>();
  externalLinkRef = createRef<HTMLAnchorElement>();

render() {
  return (
    <div className="App flex flex-col justify-center items-center">
      <h1 className="my2">Using Typescript with React</h1>
      <div className="Button__container">
        <Button
          className="Button pointer"
          ariaLabel="Click me"
          onClick={e => console.log(e.target)}
          elemRef={this.onClickButtonRef}
        >
          <h2>Issa Button</h2>
        </Button>
        <Button
          className="Button pointer"
          ariaLabel="Click me"
          to="/"
        >
          <h2>to</h2>
        </Button>

        <Button
          className="Button pointer"
          ariaLabel="Click me"
          to="google.com"
        >
          <h2>google</h2>
        </Button>
      </div>
    </div>
  )
}
}

export default App;
