import React, { createRef } from 'react';
import { AllPurposeButton, IndividuallyTypedButton } from 'components';
import { Link } from 'react-router-dom';

// function map<T>(array: Array<T>, callback: (item: T) => any) {
//   return array.forEach(callback);
// };

// const thing = map([1, 2, 3], s => s + 1);

class App extends React.Component {
  onClickButtonRef = createRef<HTMLButtonElement>();
  reactLinkRef = createRef<Link>();
  externalLinkRef = createRef<HTMLAnchorElement>();

  render() {
    return (
      <div className="App p1 flex flex-col justify-center items-center">
        <h1 className="mb1">Using Typescript with React</h1>
          <h2>All Purpose Buttons</h2>
            <AllPurposeButton
              ariaLabel="Click to open alert"
              onClick={() => alert('hola mundo!')}
              elemRef={this.onClickButtonRef}
            >
              <h2>Issa Button</h2>
            </AllPurposeButton>
            {/* <InternalLink className=""></InternalLink>
            <ExternalLink></ExternalLink> */}
            <AllPurposeButton
              // type="button"
              className="Button pointer"
              ariaLabel="Click me"
              onClick={() => 'asf'}
              elemRef={this.onClickButtonRef}
              internalTo={'afs'}
              text="hi"
            >
              <h2>to</h2>
        </AllPurposeButton>

        <AllPurposeButton
              className="Button pointer"
              ariaLabel="Click me"
              internalTo="google.com"
            >
              <h2>google</h2>
        </AllPurposeButton>
      </div>
    );
  };
};

export default App;
